import React from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

class Quiz extends React.Component {

  state = {
    title: '',
    description: '',
    spring: false,
    summer: false,
    autumn: false,
    winter: false,    

    activity: false,
    entertainment: false,
    posts: []
  };

  componentDidMount = () => {
    this.getBlogPost();
  };

  handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  getBlogPost = () => {
    axios.get('/api/')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been received!!');
      })
      .catch(() => {
        alert('Error retrieving data!!!');
      });
  }

  submit = (event) => {
    event.preventDefault();

    const payload = {
      title: this.state.title,
      description: this.state.description,
      spring: this.state.spring,
      summer: this.state.summer,
      autumn: this.state.autumn,
      winter: this.state.winter,

      activity: this.state.activity,
      entertainment: this.state.entertainment,
    };


    axios({
      url: '/api/match',
      method: 'POST',
      data: payload
    })
      .then((response) => {
            const data = response.data;
            this.setState({ posts: data });
            console.log('Data has been received!!');

      })
      .catch(() => {
        console.log('Internal server error');
      });;
  };

  resetUserInputs = () => {
    this.setState({
      title: '',
      description: '',
      spring: false,
      summer: false,
      autumn: false,
      winter: false,    
  
      activity: false,
      entertainment: false,
    });
  };

  displayBlogPost = (posts) => {

    if (!posts.length) return null;


    return posts.map((post, index) => (
      <div key={index} className="blog-post__display">
        <h3>{post.title}</h3>
        <p>{post.description}</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    //JSX
    return(
      <section  className="page-section bg-primary text-white mb-0">
        <form onSubmit={this.submit}>
        <div className="central">
        <h3>Filter blogs by subjects</h3>
          <div className="form-input checkbox">
          <label>
          Biology:
            <input
              type="checkbox"
              name="spring"
              checked={this.state.spring}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Chemistry:
            <input
              type="checkbox"
              name="summer"
              checked={this.state.summer}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>          
          <div className="form-input checkbox">
          <label>
          Physics:
            <input
              type="checkbox"
              name="autumn"
              checked={this.state.autumn}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>          
          <div className="form-input checkbox">
          <label>
          Maths:
            <input
              type="checkbox"
              name="winter"
              checked={this.state.winter}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Computer Science:
            <input
              type="checkbox"
              name="activity"
              checked={this.state.activity}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Engineering:
            <input
              type="checkbox"
              name="entertainment"
              checked={this.state.entertainment}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <button href="results">Filter</button>
          </div>
        </form>
        <div id="results" className="blog- central">
          {this.displayBlogPost(this.state.posts)}
        </div>

      </section>
    );
  }
}

export default Quiz;
