import React from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

class Quiz extends React.Component {

  state = {
    title: '',
    description: '',
    spring: false,
    summer: false,
    autumn: false,
    winter: false,    

    activity: false,
    entertainment: false,
    foodAndDrink: false,
    sightSeeing: false, 
    
    morning: false,
    afternoon: false,
    evening: false,   
    night: false,

    free: false,
    cheap: false,
    mid: false,   
    expensive: false,
    posts: []
  };

  componentDidMount = () => {
    this.getBlogPost();
  };

  handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  getBlogPost = () => {
    axios.get('/api/')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been received!!');
      })
      .catch(() => {
        alert('Error retrieving data!!!');
      });
  }

  submit = (event) => {
    event.preventDefault();

    const payload = {
      title: this.state.title,
      description: this.state.description,
      spring: this.state.spring,
      summer: this.state.summer,
      autumn: this.state.autumn,
      winter: this.state.winter,

      activity: this.state.activity,
      entertainment: this.state.entertainment,
      foodAndDrink: this.state.foodAndDrink,
      sightSeeing: this.state.sightSeeing,

      morning: this.state.morning,
      afternoon: this.state.afternoon,
      evening: this.state.evening,
      night: this.state.night,
  
      free: this.state.free,
      cheap: this.state.cheap,
      mid: this.state.mid, 
      expensive: this.state.expensive
    };


    axios({
      url: '/api/match',
      method: 'POST',
      data: payload
    })
      .then((response) => {
            const data = response.data;
            this.setState({ posts: data });
            console.log('Data has been received!!');

      })
      .catch(() => {
        console.log('Internal server error');
      });;
  };

  resetUserInputs = () => {
    this.setState({
      title: '',
      description: '',
      spring: false,
      summer: false,
      autumn: false,
      winter: false,    
  
      activity: false,
      entertainment: false,
      foodAndDrink: false,
      sightSeeing: false, 
      
      morning: false,
      afternoon: false,
      evening: false,   
      night: false,
  
      free: false,
      cheap: false,
      mid: false,   
      expensive: false
    });
  };

  displayBlogPost = (posts) => {

    if (!posts.length) return null;


    return posts.map((post, index) => (
      <div key={index} className="blog-post__display">
        <h3>{post.title}</h3>
        <p>{post.description}</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    //JSX
    return(
      <section  class="page-section bg-primary text-white mb-0">
        <form onSubmit={this.submit}>
        <div class="central">
        <h3>Filter blogs by subjects</h3>
          <div className="form-input checkbox">
          <label>
          Biology:
            <input
              type="checkbox"
              name="foodAndDrink"
              checked={this.state.foodAndDrink}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Chemistry:
            <input
              type="checkbox"
              name="sightSeeing"
              checked={this.state.sightSeeing}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>          
          <div className="form-input checkbox">
          <label>
          Physics:
            <input
              type="checkbox"
              name="sightSeeing"
              checked={this.state.sightSeeing}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>          
          <div className="form-input checkbox">
          <label>
          Maths:
            <input
              type="checkbox"
              name="morning"
              checked={this.state.morning}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Computer Science:
            <input
              type="checkbox"
              name="afternoon"
              checked={this.state.afternoon}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <div className="form-input checkbox">
          <label>
          Engineering:
            <input
              type="checkbox"
              name="afternoon"
              checked={this.state.afternoon}
              onChange={this.handleChange.bind(this)} />
          </label>
          </div>
          <button href="results">Filter</button>
          </div>
        </form>
        <div id="results" className="blog-">
          {this.displayBlogPost(this.state.posts)}
        </div>

      </section>
    );
  }
}

export default Quiz;
